import useWebSocket from 'react-use-websocket';

// In functional React component
const getSocketUrl = useCallback(() => {
  return new Promise(resolve => {
    setTimeout(() => {
      resolve('wss://echo.websocket.org');
    }, 2000);
  });
}, []);

const {
  sendMessage,
  lastMessage,
  readyState,
  getWebSocket
} = useWebSocket(getSocketUrl, STATIC_OPTIONS);